import { useParams } from "react-router-dom";
import Card from "../../UI/Card";



const MealItemDetail = () => {

    const params = useParams();
var itemDetail = [];

    const fetchItem = async () => {
        const response = await fetch(`https://market-app-43d47-default-rtdb.firebaseio.com/market/${params.id}.json`  );
        const responseData = await response.json();

        console.log(responseData);

        itemDetail = responseData;

        for (const key in responseData)
        itemDetail.push({
          id:key,
          name:responseData[key].name,
          description : responseData[key].description,
          price : responseData[key].price
        })

    }

    fetchItem();

    console.log(itemDetail.name);
    return <Card>

      <div>
        <h2>{itemDetail.name}</h2>
        <div>
          <span >{itemDetail.price}</span>
          <span>{itemDetail.description}</span>
        </div>
      </div>

    </Card>

}

export default MealItemDetail;
