import { useContext, useState } from 'react';

import {Route} from 'react-router-dom'
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import CartContext from './store/cart-context';
import MealItemDetail from './components/Meals/MealItem/MealItemDetail';
import { useDispatch, useSelector } from 'react-redux';
import { cartActions } from './redux-store';




function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const isModalVisible = useSelector(state=>state.isModalVisible);
  const dispatch = useDispatch();

  const showCartHandler = () => {
    setCartIsShown(true);
    //dispatch(cartActions.toggleModalVisibility(true));


  };

  const hideCartHandler = () => {
    setCartIsShown(false);
   // dispatch(cartActions.toggleModalVisibility(false));

  };

  return (

    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      <Header onShowCart={showCartHandler} />
      <main>
        <Route path='/' exact>
          <Meals />
        </Route>

        <Route path='/mealItem/:id' exact>
          <MealItemDetail></MealItemDetail>
        </Route>

        {/* <Route path='/' */}

      </main>
    </CartProvider>
  );
}

export default App;
