import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkit';


const initialState = { items : [], totalAmount : 0, totalQuantity : 0};

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers : {

        addItemToCart(state,action) {

        const newItem = action.payload;

        // const existingItem = state.items.filter((item) => item.id === newItem.id);
        const existingCartItemIndex = state.items.findIndex(
            (item) => item.id === newItem.id
          );

        const existingCartItem = state.items[existingCartItemIndex]; 
        state.totalQuantity = state.totalQuantity + newItem.amount; 

        console.log(newItem);




        if(!existingCartItem) {
            state.items.push(newItem);

        } else
         {

             state.totalAmount = state.totalAmount + existingCartItem.price*existingCartItem.amount;
             existingCartItem.amount++;
         }
        },


        removeItemFromCart(state,action) {

            const id = action.payload;
            console.log(id);

            const existingItem = state.items.find((item) => item.id === id);
            state.totalAmount = state.totalAmount - existingItem.price;
            state.totalQuantity--;

            if(existingItem.amount === 1) {
                state.items = state.items.filter((item)=> item.id !==  id);
            }
            else
             {
                 existingItem.amount--;
             }

          // make quantity of cartitems to 0 when confirm is called


        },
        makeCartItemsZero(state) {
            state.totalQuantity=0;
          },

        addItemToCartOnce(state) {

            const existingCartItem = state.items[existingCartItemIndex]; 
            state.totalQuantity = state.totalQuantity++; 

            state.totalAmount = state.totalAmount + existingCartItem.price*existingCartItem.amount;
            existingCartItem.amount++;



        }

    }
});



const store = createStore(cartSlice.reducer);
export default store;

export const cartActions = cartSlice.actions;
