import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkit';


const initialState = { items : [], totalAmount : 0};

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducer : {
        addItemToCart(state) {

        //   updatedTotalAmount =
        //     state.totalAmount + action.item.price * action.item.amount;

          //state.totalAmount =   state.totalAmount + action.item.price * action.item.amount;


          const existingCartItemIndex = state.items.findIndex(
            (item) => item.id === action.item.id
          );
          const existingCartItem = state.items[existingCartItemIndex];

          //let updatedItems;

          if (existingCartItem) {
            const updatedItem = {
              ...existingCartItem,
              amount: existingCartItem.amount + action.item.amount,
            };

            state.items = [...state.items];
            state.items[existingCartItemIndex] = updatedItem;
          } 
          else {
            state.items = state.items.concat(action.item);
          }
        },


        removeItemFromCart(state) {

            const existingCartItemIndex = state.items.findIndex(
               // (item) => item.id === action.id
               (item) => item.id === action.payload
              );
              const existingItem = state.items[existingCartItemIndex];
             // const updatedTotalAmount = state.totalAmount - existingItem.price;

             state.totalAmount = state.totalAmount - existingItem.price;

              //let updatedItems;
              if (existingItem.amount === 1) {
                state.items = state.items.filter(item => item.id !== action.payload);
              } else {
                const updatedItem = { ...existingItem, amount: existingItem.amount - 1 };
                state.items = [...state.items];
                state.items[existingCartItemIndex] = updatedItem;
              }


        },
    }
});



// const cartReducer = (state = initialState, action) => {


// }


const store = createStore(cartSlice.reducer);

const cartActions = cartSlice.actions;

export default store;
